package com.example.android.workoutapp;

import android.content.Intent;
import android.os.Bundle;
import android.view.Menu;
import android.view.MenuInflater;
import android.view.MenuItem;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import java.util.ArrayList;

public class MainActivity extends AppCompatActivity implements ExercisesRecyclerView.OnItemListerner {

    //Variables
    private ArrayList<String>mExerciseNames=new ArrayList<>();
    private ArrayList<String>mImageUrls=new ArrayList<>();

    private void initImageBitmaps() {

        mImageUrls.add("https://w0.pngwave.com/png/285/1014/cartoon-running-silhouette-of-man-running-png-clip-art.png");
        mExerciseNames.add("Running");

        mImageUrls.add("https://www.pinclipart.com/picdir/middle/84-840744_swimming-people-cliparts-swimming-cartoon-stick-figure-png.png");
        mExerciseNames.add("Swimming");

        mImageUrls.add("https://www.pinclipart.com/picdir/middle/84-840744_swimming-people-cliparts-swimming-cartoon-stick-figure-png.png");
        mExerciseNames.add("Cycling");

        mImageUrls.add("https://www.pinclipart.com/picdir/middle/326-3260038_men-clipart-weightlifting-stick-man-lifting-weights-png.png");
        mExerciseNames.add("Lifting");

        mImageUrls.add("https://lh3.googleusercontent.com/proxy/gsrJfceT_fhIqZjHkcZ1kCRHkHfK_FAghzst97GXD6v4RiSmG0D4P7W4UBjkQ6TtVINFhvW_y5M1b2XcI0ylegru");
        mExerciseNames.add("Baseball");

        mImageUrls.add("https://i.pinimg.com/originals/4e/82/f2/4e82f2f5fefe297c4ef2645a67053afe.jpg");
        mExerciseNames.add("Football");

        mImageUrls.add("https://www.pinclipart.com/picdir/big/133-1331729_black-and-white-football-clipart-soccer-ball-png.png");
        mExerciseNames.add("Soccer");

        mImageUrls.add("https://us.123rf.com/450wm/miceking/miceking1506/miceking150602055/40961072-stock-vector-hockey-player.jpg?ver=6");
        mExerciseNames.add("Hockey");

        mImageUrls.add("https://i.pinimg.com/originals/db/a1/62/dba162603c71cac00d3548420c52bac6.png");
        mExerciseNames.add("Basketball");

        mImageUrls.add("data:image/jpeg;base64,");
        mExerciseNames.add("Tennis");
        initRecyclerView();
    }

    private void initRecyclerView(){
        RecyclerView recyclerView = (RecyclerView) findViewById(R.id.RecylclerView);
        ExercisesRecyclerView adapter =  new ExercisesRecyclerView(this,mExerciseNames,mImageUrls,this);
        recyclerView.setAdapter(adapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));
    }

    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        MenuInflater inflater= getMenuInflater();
        inflater.inflate(R.menu.main_menu,menu);
        return true;
    }

    @Override
    public boolean onOptionsItemSelected(MenuItem item) {
        switch(item.getItemId()){
            case R.id.Profile:
                Intent p=new Intent(this,UserProfile.class);
                startActivity(p);
        }
        return super.onOptionsItemSelected(item);
    }

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        initImageBitmaps();


    }

    @Override
    public void onClick(int position) {

        Intent intent = new Intent(this,ViewAndAdd.class);
        intent.putExtra("WORKOUT_NAME",mExerciseNames.get(position));
        startActivity(intent);



    }
}
